select FGCWRYID,FGCWRYXM,JGXXID,JGBH,JGMC,JGJC,SJJG,YZBM,ZTXXID,JGZT,GJ,SF,CS,SJJGBH,SJJGMC,ZTMC,CRENAME,CRETIME, row_number() over(order by
$orderField$
CRETIME DESC
) rownum_ from ( select u.FGCWRYID,u.FGCWRYXM,u.JGXXID,u.JGBH,u.JGMC,u.JGJC,u.SJJG,u.YZBM,u.ZTXXID,u.JGZT,a.GJ,a.SF,a.CS,b.JGBH as SJJGBH,b.JGMC SJJGMC,c.ZTMC,U.CRENAME,u.CRETIME from ${SYSSCHEMA}.T_SYS_JGXX u left join ${SYSSCHEMA}.T_SYS_DZXX a on u.DZXXID = a.DZXXID left join ${SYSSCHEMA}.T_SYS_JGXX b on u.sjjg=b.jgxxid left join ${SYSSCHEMA}.T_SYS_ZTXX c on u.ztxxid=c.ztxxid
)temp
